document.addEventListener("DOMContentLoaded",()=>{initScrollReveal();initLightbox();initSmoothScroll()});function initScrollReveal(){const reveals=document.querySelectorAll(".reveal");const revealOnScroll=()=>{const windowHeight=window.innerHeight;const elementVisible=100;reveals.forEach(reveal=>{const elementTop=reveal.getBoundingClientRect().top;if(elementTop<windowHeight-elementVisible){reveal.classList.add("active")}})};window.addEventListener("scroll",revealOnScroll);revealOnScroll()}function initLightbox(){const lightbox=document.getElementById("lightbox");const lightboxImg=document.getElementById("lightbox-img");const captionText=document.getElementById("caption");const closeBtn=document.querySelector(".close-lightbox");const items=document.querySelectorAll(".gallery-item");items.forEach(item=>{item.addEventListener("click",function(){const img=this.querySelector("img");lightbox.style.display="flex";lightbox.style.flexDirection="column";lightbox.style.justifyContent="center";lightboxImg.src=img.src;captionText.innerHTML=img.alt||"Gallery Image";document.body.style.overflow="hidden"})});const closeLightbox=()=>{lightbox.style.display="none";document.body.style.overflow="auto"};closeBtn.addEventListener("click",closeLightbox);lightbox.addEventListener("click",e=>{if(e.target===lightbox){closeLightbox()}});document.addEventListener("keydown",e=>{if(e.key==="Escape"&&lightbox.style.display!=="none"){closeLightbox()}})}function initSmoothScroll(){document.querySelectorAll(".gallery-nav a").forEach(anchor=>{anchor.addEventListener("click",function(e){e.preventDefault();const targetId=this.getAttribute("href");const targetSection=document.querySelector(targetId);document.querySelectorAll(".filter-btn").forEach(btn=>btn.classList.remove("active"));this.classList.add("active");if(targetSection){const offset=130;const bodyRect=document.body.getBoundingClientRect().top;const elementRect=targetSection.getBoundingClientRect().top;const elementPosition=elementRect-bodyRect;const offsetPosition=elementPosition-offset;window.scrollTo({top:offsetPosition,behavior:"smooth"})}})})}