document.addEventListener("DOMContentLoaded",()=>{gsap.registerPlugin(ScrollTrigger);const tl=gsap.timeline({defaults:{ease:"power3.out",duration:1}});tl.to(".help-hero h1",{opacity:1,y:0}).to(".help-hero p",{opacity:1,y:0},"-=0.6").to(".search-wrapper",{opacity:1,scale:1},"-=0.6");gsap.to(".help-card",{scrollTrigger:{trigger:".help-grid",start:"top 80%"},y:0,opacity:1,duration:.8,stagger:.2,ease:"power2.out"});const container=document.querySelector(".horizontal-container");function getScrollAmount(){let racesWidth=container.scrollWidth;return-(racesWidth-window.innerWidth)}const tween=gsap.to(container,{x:getScrollAmount,ease:"none"});ScrollTrigger.create({trigger:".team-scroll-wrapper",start:"top top",end:()=>`+=${getScrollAmount()*-1}`,pin:true,animation:tween,scrub:1,invalidateOnRefresh:true});const cards=gsap.utils.toArray(".team-desc-card");cards.forEach(card=>{gsap.to(card,{opacity:1,scale:1,y:0,duration:.5,scrollTrigger:{trigger:card,containerAnimation:tween,start:"left center",toggleActions:"play none none reverse"}})});gsap.to(".cta-content",{scrollTrigger:{trigger:".help-cta",start:"top 75%"},opacity:1,y:0,duration:1});const searchInput=document.querySelector(".help-search");searchInput.addEventListener("focus",()=>{searchInput.parentElement.style.transform="scale(1.02)"});searchInput.addEventListener("blur",()=>{searchInput.parentElement.style.transform="scale(1)"})});