document.addEventListener("DOMContentLoaded",()=>{gsap.registerPlugin(ScrollTrigger,TextPlugin);const heroText="Initiating handshake protocol...\n> Identifying user location...\n> Access granted: GUEST LEVEL 1\n> Welcome to Pixel Phantoms.";gsap.to("#hero-terminal-text",{text:{value:heroText,delimiter:""},duration:4,ease:"none",onComplete:()=>{document.querySelector(".typing-cursor").style.animation="blink 1s infinite"}});gsap.to(".mission-status-bar",{opacity:1,y:-10,duration:1,delay:3.5,ease:"power2.out"});gsap.utils.toArray(".step-card").forEach((card,i)=>{gsap.to(card,{scrollTrigger:{trigger:card,start:"top 85%",toggleActions:"play none none reverse"},y:0,opacity:1,duration:.8,delay:i*.2,ease:"back.out(1.7)"})});const terminalBody=document.getElementById("live-terminal-body");const processNames=["AUTH","KERNEL","NET","SECURE","MEMBER"];const messages=["New node connection established from 192.168.x.x","Verifying encrypted signature...","Handshake successful. Token generated.","Updating local repository cache...","User 'Phantom_01' joined the server.","Deploying assets to production.","Security scan complete: 0 threats found.","Optimizing neural network weights...","Packet received: 2048 bytes.","System optimal. Ready for input."];function addLog(){if(!terminalBody)return;const now=new Date;const timeString=`[${now.getHours().toString().padStart(2,"0")}:${now.getMinutes().toString().padStart(2,"0")}:${now.getSeconds().toString().padStart(2,"0")}]`;const proc=processNames[Math.floor(Math.random()*processNames.length)];const msg=messages[Math.floor(Math.random()*messages.length)];const row=document.createElement("div");row.className="log-line";const isSuccess=msg.includes("successful")||msg.includes("optimal")||msg.includes("complete");const msgClass=isSuccess?"log-msg success":"log-msg";row.innerHTML=`
            <span class="log-time">${timeString}</span>
            <span class="log-proc">${proc}:</span>
            <span class="${msgClass}">${msg}</span>
        `;terminalBody.appendChild(row);gsap.to(row,{opacity:1,duration:.3});terminalBody.scrollTop=terminalBody.scrollHeight;if(terminalBody.children.length>15){terminalBody.removeChild(terminalBody.firstChild)}setTimeout(addLog,Math.random()*2e3+500)}addLog();const cursor=document.getElementById("cursor-highlight");if(window.matchMedia("(pointer: fine)").matches){document.addEventListener("mousemove",e=>{gsap.to(cursor,{x:e.clientX,y:e.clientY,duration:.1,ease:"power2.out"})})}});