document.addEventListener("DOMContentLoaded",()=>{gsap.registerPlugin(ScrollTrigger);initTypeWriter();initScrollReveal()});function initTypeWriter(){const titleElement=document.getElementById("dynamic-title");const textToType="Privacy_Policy !!";let index=0;const cursor=document.createElement("span");cursor.className="typing-cursor";titleElement.parentNode.insertBefore(cursor,titleElement.nextSibling);function type(){if(index<textToType.length){titleElement.innerHTML+=textToType.charAt(index);index++;setTimeout(type,100)}else{}}setTimeout(type,500)}function initScrollReveal(){gsap.utils.toArray(".reveal-text").forEach((section,i)=>{gsap.to(section,{scrollTrigger:{trigger:section,start:"top 85%",toggleActions:"play none none reverse"},y:0,opacity:1,duration:.8,ease:"power2.out"})});gsap.utils.toArray(".reveal-stamps").forEach((box,i)=>{gsap.to(box,{scrollTrigger:{trigger:box,start:"top 90%"},x:0,opacity:1,duration:1,delay:i*.2,ease:"back.out(1.7)"})})}