document.addEventListener("DOMContentLoaded",()=>{gsap.registerPlugin(ScrollTrigger);animateCards();initFilters();initWobbleToggle()});function animateCards(){gsap.fromTo(".project-card",{y:50,opacity:0,scale:.9,filter:"blur(5px)"},{y:0,opacity:1,scale:1,filter:"blur(0px)",duration:.6,stagger:.1,ease:"power2.out",clearProps:"all"})}function initFilters(){const buttons=document.querySelectorAll(".btn-glitch-filter");const cards=document.querySelectorAll(".project-card");buttons.forEach(btn=>{btn.addEventListener("click",()=>{buttons.forEach(b=>b.classList.remove("active"));btn.classList.add("active");const filterValue=btn.getAttribute("data-filter");const timeline=gsap.timeline();timeline.to(cards,{duration:.2,opacity:0,scale:.95,filter:"brightness(2) blur(10px)",ease:"power1.in",onComplete:()=>{cards.forEach(card=>{const cardCategory=card.getAttribute("data-category");if(filterValue==="all"||filterValue===cardCategory){card.style.display="flex"}else{card.style.display="none"}})}});timeline.to(cards,{duration:.4,opacity:1,scale:1,filter:"brightness(1) blur(0px)",ease:"back.out(1.7)",stagger:{amount:.2,from:"random"},clearProps:"filter, opacity, scale"})})})}function initWobbleToggle(){const toggle=document.getElementById("wobble-toggle");const card=document.getElementById("wocs-card");if(toggle&&card){toggle.addEventListener("change",()=>{if(toggle.checked){card.classList.add("wobbling")}else{card.classList.remove("wobbling")}})}}