document.addEventListener("DOMContentLoaded",()=>{initDate();initScrollReveal()});function initDate(){const dateElement=document.getElementById("current-date");if(dateElement){const options={year:"numeric",month:"long",day:"numeric"};const today=new Date;dateElement.textContent=today.toLocaleDateString("en-US",options)}}function initScrollReveal(){const reveals=document.querySelectorAll(".reveal");const revealOnScroll=()=>{const windowHeight=window.innerHeight;const elementVisible=100;reveals.forEach(reveal=>{const elementTop=reveal.getBoundingClientRect().top;if(elementTop<windowHeight-elementVisible){reveal.classList.add("active")}})};revealOnScroll();window.addEventListener("scroll",revealOnScroll)}