document.addEventListener("DOMContentLoaded",function(){const themeSwitch=document.getElementById("theme-switch");const htmlElement=document.documentElement;const savedTheme=localStorage.getItem("theme")||"dark";htmlElement.setAttribute("data-theme",savedTheme);if(savedTheme==="light"){themeSwitch.checked=true}themeSwitch.addEventListener("change",function(){if(this.checked){htmlElement.setAttribute("data-theme","light");localStorage.setItem("theme","light")}else{htmlElement.setAttribute("data-theme","dark");localStorage.setItem("theme","dark")}});console.log("Pixel Phantoms website loaded with theme: "+savedTheme)});